import React, { useEffect, useState } from 'react';
import { Upload, Button, message, Image, Space } from 'antd';
import { LoadingOutlined, PlusOutlined, UploadOutlined } from '@ant-design/icons';

import './index.css';
import styles from './style.less';
import { normalizeImgs } from '@/utils/utils';

function beforeUpload(file) {
  const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
  if (!isJpgOrPng) {
    message.error('You can only upload JPG/PNG file!');
  }
  const checkImgSize = file.size / 1024 / 1024 < 5;
  if (!checkImgSize) {
    message.error('Image must smaller than 5MB!');
  }
  return isJpgOrPng && checkImgSize;
}

const failureImg =
  'data:image/png;base64,';

const ImageUploader = ({ multiple = false, onChange: onChangeForm, disable = false, ...props }) => {
  const [fileList, setFileList] = useState(props.fileList != null ? props.fileList : null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setFileList(props.fileList != null ? props.fileList : null);
  }, [props.fileList]);

  const defaultProps = {
    beforeUpload,
    onChange: (info) => {
      if (info.file.status === 'uploading') {
        setLoading(true);
        return;
      }
      if (info.file.status === 'done') {
        console.log('info',info)
        if(multiple) {
          setFileList(normalizeImgs(fileList ? [...fileList, info.file] : [info.file]));
          onChangeForm({ fileList: normalizeImgs(fileList ? [...fileList, info.file] : [info.file]) });
        } else {
          onChangeForm({ fileList: [info.file] });
          setFileList([info.file]);
        }
        message.success('Upload thành công');
        setLoading(false);
        return;
        // const fileData = multiple ? info.fileList : info.file;
        // onChangeForm({ fileList: multiple ? fileData : [fileData] });
        // setFileList(multiple ? fileData : [fileData]);
        // message.success('Upload thành công');
        // setLoading(false);
        // return;
      }
      if (info.file.status === 'error') {
        message.error('Lỗi khi upload file');
        setLoading(false);
      }
    },
  };

  const fileResList = () => {
    console.log('FL',fileList);
    if (fileList == null) {
      return null;
    }
    if (fileList && !multiple) {
      const { name, response: imgUrl } = fileList[0];
      const src = imgUrl !== undefined ? imgUrl : fileList;
      return (
        <Image
          className={styles.imageContainer}
          style={{ width: '102px', height: '102px' }}
          width={102}
          height={102}
          alt={name}
          src={src}
          fallback={failureImg}
        />
      );
    }
    if (fileList && typeof fileList !== 'string' && multiple) {
      let fl = normalizeImgs(fileList);
      var imgs = [];
      for (let i = 0; i < fl.length; i++) {
        let src = fl[i];
        imgs.push(<Image
          className={styles.imageContainer}
          style={{ width: '102px', height: '102px' }}
          width={102}
          height={102}
          alt={`Anh ${i}`}
          src={src}
          fallback={failureImg}
        />)
      }
      return imgs;
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Tải lên</div>
    </div>
  );

  return (
    <Space direction="horizontal" style={{ display: 'flex' }}>
      {fileResList()}
      {!disable && (
        <Upload
          // name="avatar"
          className="avatar-uploader"
          listType="picture-card"
          action={`https://webapp-230621221913.azurewebsites.net/api/firebase/files`}
          showUploadList={multiple}
          style={{ width: 250 }}
          multiple={multiple}
          // {...props}
          {...defaultProps}
        >
          {uploadButton}
        </Upload>
      )}
    </Space>
  );
};

export default ImageUploader;
